rule "Strip message parts stored with extractors"
when
 has_field("lib_file") && has_field("log_timestamp") && has_field("message_level") && has_field("module")
then
 let msg = to_string($message.message);
 let formatted_msg = regex_replace("[^\\]]* [A-Z]*: (.*)\\(.*", msg, "$1");
 set_field("message", formatted_msg);
end
